import jxl.*
import jxl.write.*
import com.eviware.soapui.support.XmlHolder
Workbook workbook = Workbook.getWorkbook(new File ( "E:jxl_for_soap/Data.xls"))
Sheet sheet = workbook.getSheet(0)
def rowCount = sheet.getRows()
def colCount  = sheet.getColumns()

 for(i=0; i<rowCount; i++){
 	a=sheet.getCell(0,i)
 	b=a.getContents()
 	def myWord=b.toString()
 	c=sheet.getCell(1,i)
 	d=c.getContents()
 	def expectedWord = d.toString()

 	log.info(myWord)
 	log.info(expectedWord)

 	testRunner.testCase.setPropertyValue("MYWORD", myWord)
 	testRunner.testCase.setPropertyValue("EXPECTEDWORD", expectedWord )

 	testRunner.runTestStepByName("Checking Request")
 	}
 	testRunner.testCase.getSetupScript();
 	testRunner.testCase.getTearDownScript();
